                     CFDictionaryRef userInfo)
{
    NSLog(@"CFNotificationCenterRef====%@====%@",[NSThread currentThread],name);
}

- (void)viewDidLoad {
    [super viewDidLoad];
    self.notifiQueue = @[].mutableCopy;
    self.thread = [NSThread currentThread];
    self.lock = [[NSLock alloc] init];
    self.machPort = [[NSMachPort alloc] init];
    self.machPort.delegate = self;

    // 往当前线程的run loop添加端口源
    // 当Mach消息到达而接收线程的runloop没有运行时，则内核会保存这条消息，直到下一次进入runloop
    [[NSRunLoop currentRunLoop] addPort:self.machPort forMode:(__bridge NSString *)kCFRunLoopCommonModes];

    [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(processNotification:) name:@"TestNotification" object:nil];

    //CFNotificationCenterRef:通过CFNotificationCenterRef发送出来的通知无论是在子线程还是主线程最终都会h转发到主线程中
    CFNotificationCenterRef center = CFNotificationCenterGetDarwinNotifyCenter();

    CFNotificationSuspensionBehavior behavior = CFNotificationSuspensionBehaviorDeliverImmediately;

    CFNotificationCenterAddObserver(center,
                                    NULL,
                                    Callback,
                                    CFSTR("notification.identifier"),
                                    NULL,
                                    behavior);
    //NSNotificationCenter:子线程发出的通知依旧在子线程
    [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(doSome:) name:@"notification.identifier" object:nil];
}

- (void)doSome:(NSNotification *)notifi {
    NSLog(@"NSNotificationCenter====%@====%@",[NSThread currentThread],notifi.name);
}

- (void)processNotification:(NSNotification *)notifi {
    if ([NSThread currentThread] != self.thread) {
        NSLog(@"NSMachPort if====%@====%@",[NSThread currentThread],notifi.name);
        [self.lock lock];
        [self.notifiQueue addObject:notifi];
        [self.lock unlock];
        //通过MachPort转发子线程通知到主线程
        [self.machPort sendBeforeDate:[NSDate date]
                                   components:nil
                                         from:nil
                                     reserved:0];
    } else {
        NSLog(@"NSMachPort else====%@====%@", [NSThread currentThread],notifi.name);
    }
}

// MARK: - NSMachPortDelegate

- (void)handleMachMessage:(void *)msg {
    [self.lock lock];
    NSLog(@"self.notifiQueue ==== %@",self.notifiQueue);
    while ([self.notifiQueue count]) {
        NSNotification *notification = [self.notifiQueue objectAtIndex:0];
        [self.notifiQueue removeObjectAtIndex:0];
        [self.lock unlock];
        [self processNotification:notification];
        [self.lock lock];
    };
    [self.lock unlock];
}

@end

// 其它地方
dispatch_async(dispatch_get_global_queue(DISPATCH_QUEUE_PRIORITY_DEFAULT, 0), ^{
        void *Object;
        CFDictionaryRef userInfo;
        CFNotificationCenterRef center = CFNotificationCenterGetDarwinNotifyCenter();
        CFNotificationCenterPostNotification(center,
                                             CFSTR("notification.identifier"),
                                             Object,
                                             userInfo,
                                             true);
        NSLog(@"CFNotificationCenterRef====%@====%p",[NSThread currentThread],Object);
        [[NSNotificationCenter defaultCenter] postNotificationName:@"notification.identifier" object:nil];
        [[NSNotificationCenter defaultCenter] postNotificationName:@"TestNotification" object:nil];
        NSLog(@"NSNotificationCenter====%@",[NSThread currentThread]);
    });
```



上面的代码就是我们通常所做的事情：添加一个通知监听者，定义一个回调，并在所属对象释放时移除监听者；然后在程序的某个地方post一个通知。简单明了，如果这一切都是发生在一个线程里面，或者至少dealloc方法是在-postNotificationName:的线程中运行的\(注意：NSNotification的post和转发是同步的\)，那么都OK，没有线程安全问题。但如果dealloc方法和-postNotificationName:方法不在同一个线程中运行时，会出现什么问题呢？

我们再改造一下上面的代码：



## NSNotificationCenter引发的线程安全问题



```
#pragma mark - Poster

@interface Poster : NSObject

@end

@implementation Poster

- (instancetype)init
{
    self = [super init];

    if (self)
    {
        [self performSelectorInBackground:@selector(postNotification) withObject:nil];
    }

    return self;
}

- (void)postNotification
{
    [[NSNotificationCenter defaultCenter] postNotificationName:TEST_NOTIFICATION object:nil];
}

@end

#pragma mark - Observer

@interface Observer : NSObject
{
    Poster  *_poster;
}

@property (nonatomic, assign) NSInteger i;

@end

@implementation Observer

- (instancetype)init
{
    self = [super init];

    if (self)
    {
        _poster = [[Poster alloc] init];

        [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(handleNotification:) name:TEST_NOTIFICATION object:nil];
    }

    return self;
}

- (void)handleNotification:(NSNotification *)notification
{
    NSLog(@"handle notification begin");
    sleep(1);
    NSLog(@"handle notification end");

    self.i = 10;
}

- (void)dealloc
{
    [[NSNotificationCenter defaultCenter] removeObserver:self];

    NSLog(@"Observer dealloc");
}

@end

#pragma mark - ViewController

@implementation ViewController

- (void)viewDidLoad {
    [super viewDidLoad];

    __autoreleasing Observer *observer = [[Observer alloc] init];
}

@end
```



这段代码是在主线程添加了一个TEST\_NOTIFICATION通知的监听者，并在主线程中将其移除，而我们的NSNotification是在后台线程中post的。在通知处理函数中，我们让回调所在的线程睡眠1秒钟，然后再去设置属性i值。这时会发生什么呢？我们先来看看输出结果：

```
2015-03-14 00:31:41.286 SKTest[932:88791] handle notification begin
2015-03-14 00:31:41.291 SKTest[932:88713] Observer dealloc
2015-03-14 00:31:42.361 SKTest[932:88791] handle notification end
(lldb) 

// 程序在self.i = 10处抛出了"Thread 6: EXC_BAD_ACCESS(code=EXC_I386_GPFLT)"
```

经典的内存错误，程序崩溃了。其实从输出结果中，我们就可以看到到底是发生了什么事。我们简要描述一下：

1. 当我们注册一个观察者是，通知中心会持有观察者的一个弱引用，来确保观察者是可用的。
2. 主线程调用dealloc操作会让Observer对象的引用计数减为0，这时对象会被释放掉。
3. 后台线程发送一个通知，如果此时Observer还未被释放，则会向其转发消息，并执行回调方法。而如果在回调执行的过程中对象被释放了，就会出现上面的问题。

当然，上面这个例子是故意而为之，但不排除在实际编码中会遇到类似的问题。虽然NSNotificationCenter是线程安全的，但并不意味着我们在使用时就可以保证线程安全的，如果稍不注意，还是会出现线程问题。

那我们该怎么做呢？这里有一些好的建议：

1. 尽量在一个线程中处理通知相关的操作，大部分情况下，这样做都能确保通知的正常工作。不过，我们无法确定到底会在哪个线程中调用dealloc方法，所以这一点还是比较困难。
2. 注册监听都时，使用基于block的API。这样我们在block还要继续调用self的属性或方法，就可以通过weak-strong的方式来处理。具体大家可以改造下上面的代码试试是什么效果。
3. 使用带有安全生命周期的对象，这一点对象单例对象来说再合适不过了，在应用的整个生命周期都不会被释放。
4. 使用代理。

# 小结

NSNotificationCenter虽然是线程安全的，但不要被这个事实所误导。在涉及到多线程时，我们还是需要多加小心，避免出现上面的线程问题。想进一步了解的话，可以查看[Observers and Thread Safety](http://inessential.com/2013/12/20/observers_and_thread_safety)。

